import{SFX_VOLUME as t}from"./constants.js";export function qs(t,e=document){return e.querySelector(t)}export function qsa(t,e=document){return[...e.querySelectorAll(t)]}export function getHealthBarColor({hue:t,saturation:e,lightness:o},n){return{hue:2+(t-2)*n,saturation:+(90+(e-90)*n).toFixed(1),lightness:+(49+(o-49)*n).toFixed(1)}}export function valuesToHSL({hue:t,saturation:e,lightness:o}){return`hsl(${t}, ${e}%, ${o}%)`}export function setSfxVolume(e,o=t){for(const t in e)e[t].volume=o}export function getStatsFromLocalStorage(){return{bestTime:JSON.parse(localStorage.getItem("best-time")),bestLives:JSON.parse(localStorage.getItem("best-lives"))}}export function formatTime(t){if(null==t)return;let e=new Date(t);return`${e.getMinutes()}: ${e.getSeconds()}`}export function injectControlsInHowToPlay(t){qsa("[data-controls-span]").forEach((e=>e.textContent=t[e.dataset.controlsSpan]))}